import React from 'react'
import { Checkbox } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import { toggleCheck } from '../features/tasksSlice';

const TaskItem = (props) => {
  const { name, done, id } = props;
  const dispatch = useDispatch();

  const handleCheck = () => {
    dispatch(toggleCheck({id: id, done: !done}))
  }

  return (
    <div style={{display: "flex", alignItems: "center", backgroundColor: "yellow", width: "80%", height: "40px", margin: "12px 10px"}}>
      <Checkbox
        color="primary"
        checked={done}
        onChange={handleCheck}
        inputProps={{ 'aria-label': 'primary checkbox' }}
      />
      <p className={done && 'todoItem--done'}>{name}</p>
    </div>
  )
}

export default TaskItem
