import React from 'react'
import { styles } from './WelcomeScreen';
import InputTask from '../Atoms/InputTask';
import TodoItem from '../Atoms/TodoItem';
import { useSelector } from 'react-redux'
import { selectTodoList } from '../../features/todoSlice';
import { useParams } from 'react-router';
import { makeStyles, Box } from '@material-ui/core';
import Paper from '../../assets/paper-texture.jpg';

const useStyles = makeStyles({
  container: {
    paddingTop: "15px",
    backgroundImage: `url(${Paper})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "100%",
    flex: "1",
  },
});

const TODOlistScreen = () => {
  const classes = useStyles();
  const todoList = useSelector(selectTodoList);
  const state = useSelector(state => state);
  const { id } = useParams();

  console.log(state);

  return (
    <Box component="div" style={styles}>
      <Box component="div" style={{ textAlign: "center", height: "700px", width: "500px", display: "flex", flexDirection: "column", alignItems: "center", backgroundColor: "white" }} className="list__container">
        <Box component="div" className={classes.container}>
          <InputTask />
          {
            todoList.map(item => (
              <TodoItem
                activeId={id}
                name={item.item}
                done={item.done}
                id={item.id}
                key={item.id}
              />
            ))
          }
        </Box>
      </Box>
    </Box>
  )
}

export default TODOlistScreen
