import { Button, Checkbox } from '@material-ui/core';
import React from 'react'
import { useDispatch } from 'react-redux';
import { deleteTodo, updateTodo } from '../../features/todoSlice';

const Todo = (props) => {
  const {text, done, id} = props;
  const dispatch = useDispatch();
  const toggle = () => {
    dispatch(
      updateTodo ({
        id: id,
        changes: { done: !done }
      })
    );
  };

  const deleteItem = () => {
    dispatch(deleteTodo(id));
  };

  return (
    <div style={{ display: "flex", alignItems: "center", backgroundColor: "yellow", width: "80%", height: "40px", margin: "12px 10px" }}>
      <Checkbox
        color="primary"
        checked={done}

        inputProps={{ 'aria-label': 'primary checkbox' }}
      />
      <p>{text}</p>
      <Button>Delete</Button>
      <Button>Edit</Button>
    </div>
  )
}

export default Todo
