import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  todoList: []
}

const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    saveTask: (state, action) => {
      const newTask = {
        id: Date.now(),
        name: action.payload.name,
        done: false

      };
      state.todoList.push(newTask)
    },

    setCheck: (state, action) => {
      state.todoList.map(item => {
        if (action.payload === item.id) {
          if (item.done === true) {
           item.done = false
          } else {
            item.done = true
          }
        }
      })
    }
  }
});

export const { saveTask, setCheck } = tasksSlice.actions
export const selectTaskList = state => state.tasks.todoList
export default tasksSlice.reducer
