import { Button, Checkbox, Link, makeStyles } from '@material-ui/core';
import React from 'react'
import { useDispatch } from 'react-redux';
import { setCheck, removeTodos } from '../../features/todoSlice';

const useStyles = makeStyles({
  container: {
    display: "flex",
    alignItems: "center",
    width: "80%",
    backgroundColor: "white",
    margin: "6px 4px",
    height: "40px",
    borderRadius: "12px"
  },
  p: props => ({
    backgroundColor: props.done ? 'red' : 'white'
  })
});

const TodoItem = (props) => {
  const {name, done, id } = props;
  const classes = useStyles({ done });
  const dispatch = useDispatch()
  const removeTodo = () => {
    dispatch(removeTodos(id))
  }
  const handleCheck = () => {
    dispatch(setCheck(id))
  }
  return (
    <div className={classes.container}>
      <Checkbox
        checked={done}
        color="primary"
        onChange={handleCheck}
        inputProps={{ 'aria-label' : "secondary checkbox"}}
      />
      <p
        className={classes.p}
        done={done}
      >{name}</p>
      <Link to={`/list/${id}/edit`}>
        <Button color="primary">Edit</Button>
      </Link>
      <Button color="secondary" onClick={() => removeTodo()}>Delete</Button>
    </div>
  )
}

export default TodoItem
