import { Button, Input } from '@material-ui/core'
import React, { useState } from 'react'
import { connect } from 'react-redux'
import { addTodos, removeTodos } from '../../features/reducer';

const mapStateToProps = (state) => {
  return {
    todos: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addTodo: (obj) => dispatch(addTodos(obj)),
    removeTodo: (id) => dispatch(removeTodos(id)),
  }
}
const Todos = (props) => {
  const [todo, setTodo] = useState('');
  const handleChange = (e) => {
    setTodo(e.target.value)
  }
  console.log("todo text", todo)
  return (
    <div>
      <Input onChange={(e) => handleChange(e)} />
      <Button onClick={() => props.addTodo({ id: Math.floor(Math.random()*1000), item: todo, done: false})}>+</Button>
      <ul>
        {props.todos.map(item => {
          return (
             <li key={item.id}>
               {item.item}{" "}
               <button onClick={() => props.removeTodo(item.id)}>Delete</button>{" "}
               </li>
        );
        })}
      </ul>
    </div>
  )
}

export default connect(mapStateToProps, mapDispatchToProps)(Todos);
