import React from 'react'
import ListInput from '../Atoms/Input'
import TaskItem from '../TaskItem'
import { taskSelector } from '../../features/tasksSlice';
import { styles } from './WelcomeScreen';
import { useSelector } from 'react-redux';

const TODOlistScreen = () => {
  const allTasks = useSelector(taskSelector.selectEntities)
  const tasksList = [];
  for (const id in allTasks) {
    if (Object.hasOwnProperty.call(allTasks, id)) {
      const taskItem = allTasks[id];
      tasksList.push(<TaskItem
        key={taskItem.id}
        task={taskItem.task}
        done={taskItem.done}
        id={taskItem.id} />
      );
    }
  }

  return (
    <div style={styles}>
      <div style={{ height: "700px", width: "500px", display: "flex", flexDirection: "column", alignItems: "center", backgroundColor: "white", padding: "20px" }} className="list__container">
        <div style={{ display: "flex", flexDirection: "column", alignItems: "center", width: "100%", flex: "1" }} className="taskList__container">
          {
            tasksList
          }
        </div>
      </div>
      <ListInput />
    </div>
  )
}

export default TODOlistScreen
