import React from 'react'
import Box from '@material-ui/core/Box';
import { Button, Container } from '@material-ui/core';
import { styled } from '@material-ui/core';
import { height } from '@material-ui/system';

const StartButton = styled(Button)({
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
  border: 0,
  borderRadius: 3,
  boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  color: 'white',
  height: 48,
  padding: '0 30px',
})

const WelcomeScreen = () => {
  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh", background: "radial - gradient(circle, rgba(215, 224, 235, 1) 7 %, rgba(86, 92, 236, 1) 100 %)"}}>
      <Box width="25%" display="flex" justifyContent="center" alignItems="center" flexDirection="column" height="500px" component="div" color="white" bgcolor="palevioletred" p={1}>
        <h1>TODO List</h1>
        <h2>Organize your life</h2>
        <StartButton>Create a list</StartButton>
      </Box>
    </div>

  )
}

export default WelcomeScreen
